
public class OddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=3;
		
		for(int i=1; i<=10; i++)
		{
		if(i%2==0)
		{
			System.out.println(i + " is an even number");
		}
		else
		{
			System.out.println(i + " is not an even number i.e an odd number");
		}
		}

	}

}
